import React, { useCallback, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { Col, Row, Typography, Form, Input, Button, message } from 'antd';
// import { useInput } from '../../../util/useInput';

const Apply = () => {
    const { latitude, longitude, } = useSelector(state => state.location);
    const { isLoggeIn } = useSelector(state => state.user);
    // if (!isLoggeIn) {
    //     message.error("로그인 후 이용하여주세요")
    // }
    let message;
    const onSubmitForm = useCallback((e) => {
        e.preventDefault();
    })
    useEffect(()=>{
        const options = {
            center: new kakao.maps.LatLng(latitude, longitude),
            level: 8
        };

        const temp = new kakao.maps.Map(document.getElementById("register_map"), options);
        temp.addControl(new kakao.maps.MapTypeControl(), kakao.maps.ControlPosition.TOPRIGHT);
        var geocoder = new kakao.maps.services.Geocoder();
        let marker = new kakao.maps.Marker({ 
            position: temp.getCenter() 
        }); 
        marker.setMap(temp);
        // function searchDetailAddrFromCoords(coords, callback) {
        //     // 좌표로 법정동 상세 주소 정보를 요청합니다
        //     geocoder.coord2Address(coords.getLng(), coords.getLat(), callback);
        // }
        // kakao.maps.event.addListener(temp, 'click', function(mouseEvent) {
        //     searchDetailAddrFromCoords(mouseEvent.latLng, function(result, status) {
        //         if (status === kakao.maps.services.Status.OK) {
        //             console.log(result);
        //             let detailAddr = !!result[0].road_address ? '<div>도로명주소 : ' + result[0].road_address.address_name + '</div>' : '';
        //             detailAddr += '<div>지번 주소 : ' + result[0].address.address_name + '</div>';
        //         }
        //     });
        // });
    },[])

    return (
        <div style={{ width: '450px', margin: "0 auto", border: "1px solid #dadce0", borderRadius: "8px", padding:"40px 10px" }}>
            <Row gutter={[0, 16]}>
                <Col style={{ width: '100%', textAlign: "center" }}>
                    <Typography.Title level={3} style={{ color: "#202124" }}>구장 등록하기</Typography.Title>
                    <Typography style={{ color: "#202124", fontWeight: "normal" }}>구장 위치 찾기</Typography>
                </Col>
            </Row>
            <Row gutter={[0, 16]}>
                <Col span={22} offset={1}>
                    <div id="register_map" style={{width:"100%", height:"480px"}}>
                    </div>
                </Col>
            </Row>
            <Row>
                <Col span={24} style={{textAlign:"center"}}>
                    <Button type="primary" shape="round" htmlType="button" onClick={onSubmitForm}>다음단계</Button>
                </Col>
            </Row>
        </div>
    );
}

export default Apply;
