import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Col, Row, Typography, Button, Form, Input, TimePicker, Radio, Select, Upload} from 'antd';
import { PlusOutlined} from '@ant-design/icons';

const TeamRegister = () => {
    let data;
    const [, forceUpdate] = useState(); 
    const [form] = Form.useForm();

    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    };
    const onSubmitForm = (values)=>{
        console.log(values);
        console.log('------------------------------------');
    }
    const multipleSpecaility = [
        <Select.Option key={"1"}>잔디구장</Select.Option>,
        <Select.Option key={"2"}>우레탄구장</Select.Option>,
        <Select.Option key={"3"}>플라스틱 인도어구장</Select.Option>,
        <Select.Option key={"4"}>샤워실</Select.Option>,
        <Select.Option key={"5"}>근처 편의점</Select.Option>,
        <Select.Option key={"6"}>탈의실</Select.Option>,
        <Select.Option key={"7"}>대기용 좌석</Select.Option>,
    ];
    const normFile = e => {
        console.log('Upload event:', e);
        if (Array.isArray(e)) {
            return e;
        }
        return e && e.fileList;
    };
    const uploadButton = (
    <div>
        <PlusOutlined />
        <div className="ant-upload-text">Upload</div>
    </div>
    );
    useEffect(() => {
        forceUpdate({});
    }, []);
    return (
        <div style={{ width: '430px', margin: "0 auto", border: "1px solid #dadce0", borderRadius: "8px", padding:"30px 10px" }}>
            <Row gutter={[0, 16]}>
                <Col style={{ width: '100%', textAlign: "center" }}>
                    <Typography.Title level={3} style={{ color: "#202124" }}>팀 등록하기</Typography.Title>
                </Col>
            </Row>
            <Row gutter={[0, 16]}>
                <Col span={22} offset={1}>
                    <Form 
                    {...formItemLayout}
                    layout="horizontal"
                    form={form}
                    initialValues={{location:data? data[2]:'', recruit:'N'}}
                    onFinish={onSubmitForm}
                    scrollToFirstError
                    >
                        <Form.Item
                        name="title"
                        label="팀명"
                        rules={[{required:true, message:"팀명을 입력하여주세요"}]}
                        >
                            <Input placeholder="팀명을 입력해주세요"/>
                        </Form.Item>
                        <Form.Item
                        name="location"
                        label="팀 활동 지역"
                        rules={[{required:true, message:"팀 활동 지역을 입력하여주세요"}]}
                        >
                            <Input placeholder="활동 지역을 입력해주세요"/>
                        </Form.Item>
                        <Form.Item
                        name="time"
                        label="모임시간"
                        rules={[{required:true, message:"주로 모이는 요일과 시간을 적어주세요"}]}
                        >
                            <Input placeholder="ex)매주 수요일 저녁8시부터"/>
                        </Form.Item>
                        <Form.Item
                        name="description"
                        label="팀 소개"
                        >
                            <Input.TextArea placeholder="팀을 소개해 주세요!" autoSize/>
                        </Form.Item>
                        <Form.Item
                        name="recruit"
                        label="회원 모집여부"
                        >
                            <Radio.Group>
                                <Radio value="Y">회원 모집</Radio>
                                <Radio value="N" checked>회원 비모집</Radio>
                            </Radio.Group>
                        </Form.Item>
                        <Form.Item
                        name="special"
                        label="특징"
                        >
                            <Select mode="tags" placeholder="구장의 다양한 특징을 선택해주세요">
                                {multipleSpecaility}
                            </Select>
                        </Form.Item>
                        <Form.Item
                        name="picture"
                        label="팀 사진"
                        valuePropName="fileList"
                        getValueFromEvent={normFile}
                        >
                            <Upload name="upload" listType="picture-card">
                                {uploadButton}
                            </Upload>

                        </Form.Item>

                        <Form.Item
                        shouldUpdate
                        wrapperCol={{
                        span: 18,
                        offset: 6
                        }}
                        style={{marginBottom:0}}
                        >
                            {() => (
                            <Button
                                type="primary"
                                htmlType="submit"
                                shape="round"
                                disabled={
                                    !form.isFieldsTouched(["title", "size", "time"],true) ||
                                    form.getFieldsError().filter(({ errors }) => errors.length).length
                                }
                            >
                                등록하기
                            </Button>
                            )}
                        </Form.Item>
                    </Form>
                </Col>
            </Row>
        </div>
    );
};

export default TeamRegister;
