import React, {useEffect, useCallback } from 'react';
import { useRouter, withRouter, } from 'next/router'
import Router from 'next/router';
import {useDispatch, useSelector} from 'react-redux';
import {Row, Col, List, Skeleton, Typography, Button, Tag} from 'antd';
import {SEARCH_TEAMS_REQUEST} from '../../reducers/team'
import {MessageOutlined, LikeOutlined, StarOutlined} from '@ant-design/icons'

const Search = (props) =>{
    const dispatch = useDispatch();
    const {teamList, isSearched, query, isSearching } = useSelector(state => state.team);
    let hashPage = 1;
    useEffect(
        ()=>{
            dispatch({type:SEARCH_TEAMS_REQUEST, data:{"query": props.router.query.q}});
        }
    ,[props.router.query.q]);

    useCallback(
        ()=>{
            hashPage = window.location.hash.substr(1);
        },[hashPage]
    )

    return(
        <div>
            <Row>
                <Col xs={{ span: 24 }} sm={{ span: 22, offset: 1 }} md={{ span: 18, offset: 3 }}>
                    <Typography.Title level={4}>"{query}"에 대한 검색 결과입니다.</Typography.Title>

                    {
                        !isSearched ?
                        <List loading={true} />
                        :
                        <List
                            loading={isSearching}
                            itemLayout="horizontal"
                            pagination={{onChange:page=>{window.location.hash = page}, pageSize: 1, defaultCurrent:hashPage}}
                            bordered={false}
                            dataSource={teamList}
                            size="small"
                            footer={
                                <div>
                                    <b>{teamList.length}건</b>의 검색결과
                                </div>
                            }
                            renderItem={item=>(
                                <List.Item
                                    key={item.req}

                                >
                                    <List.Item.Meta 
                                        title={<Button type="link" onClick={()=>Router.push(`/team/${item.req}`)}>{item.name}</Button> }
                                        description={
                                            <div>
                                                <Tag key={item.location}>#{item.location}</Tag>
                                                {item.recruit === "Y" &&<Tag key={item.recruit}>#모집중</Tag> }
                                            </div>
                                        }
                                    />
                                        {item.description}
                                </List.Item>
                                )
                            }
                        />
                    }

                </Col>
            </Row>
        </div>
    );
}
export default withRouter(Search);
