import React, { useEffect } from 'react';
import { message, Button, Col, Row } from 'antd';
import { LoadingOutlined, SearchOutlined, InfoCircleOutlined } from '@ant-design/icons';
import { getLocation } from '../util/getLocation';
import { useSetter } from '../util/useSetter';
const Maps = () => {
    const [lat, setLat] = useSetter('37.5795876');
    const [lng, setLng] = useSetter('126.9636324');
    let options;
    let map;
    useEffect(
        () => {
            //최초 마운트 시
            // 지도 위치에 따른 데이터 가져와야 해 redux-saga
            (async () => {
                try {
                    const arr = await getLocation();
                    console.log(arr);
                    message.destroy();
                    if (arr[0] === "success") {
                        setLat(arr[2]);
                        setLng(arr[3]);
                        message.success(arr[1]);
                    } else if (arr[0] === "warn") {
                        message.warn(arr[1], 4);
                    } else {
                        message.error(arr[1], 4);
                    }
                    options = {
                        center: new kakao.maps.LatLng(lat, lng),
                        level: 8
                    };
                    map = new kakao.maps.Map(document.getElementById("mapContainer"), options);
                    map.addControl(new kakao.maps.MapTypeControl(), kakao.maps.ControlPosition.TOPRIGHT);
                    kakao.maps.event.addListener(map, 'dragend', function () {
                        let latlng = map.getCenter();
                        setLat(latlng.getLat());
                        setLng(latlng.getLng());
                    });
                } catch (error) {
                    console.log(error);
                }

            })();
        }, []
    );
    return (
        <div id="mapContainer" style={{ height: '70vh', textAlign: 'center' }}>
            {/* <LoadingOutlined style={{ margin: '0 auto', width: '10vh', height: '10vh' }} /> */}
            <Row align="middle" gutter={{ sm: 10, md: 25 }} style={{ height: '100%', height: '100%' }}>
                <Col span={8} offset={4} sm={{ span: 16, offset: 4 }}>
                    <Button type="primary" icon={<SearchOutlined />} size="large" shape="circle">현재위치정보 이용하여 둘러보기</Button>
                </Col>
                <Col span={8} sm={{ span: 16, offset: 4 }}>
                    <Button size="large" icon={<InfoCircleOutlined />} shape="round">구장 둘러보기</Button>
                </Col>
            </Row>
        </div>
    );
};

export default Maps;
