import React, { useCallback, useEffect, useState } from 'react';
import { Button, Input, Modal, Form } from 'antd';
import { useInput } from '../util/useInput';
import { useDispatch, useSelector } from 'react-redux';
import { LOG_IN_REQUEST } from '../reducers/user';
import { UserOutlined, LockOutlined } from '@ant-design/icons';

const LoginForm = (props) => {
    // const [id, onChangeId] = useInput('');
    // const [password, onChangePassword] = useInput('');
    const [form] = Form.useForm();
    const [, forceUpdate] = useState(); 

    const { visible, setVisible } = props;
    const dispatch = useDispatch();
    const { isLoggingIn, isLoggedIn } = useSelector(state => state.user);

    const onHandleCancel = (() => {
        setVisible(false);
    })
    const onSubmitForm = useCallback((values) => {
        console.log('------------------------------------');
        console.log(values);
        console.log('------------------------------------');
        dispatch({
            type: LOG_IN_REQUEST,
            data: {
                ...values
            },
        });
    }, []);
    const formItemLayout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 14,
        },
    };
    useEffect(
        ()=>{
            if(isLoggedIn){
                setVisible(false);
                //useState에 있는 id랑 password없애야 해
            }
        },[isLoggedIn]
    )
    useEffect(() => {
        forceUpdate({});
    }, []);
    return (
        <Modal
            title="FutChall로그인"
            visible={visible}
            onOk={onSubmitForm}
            onCancel={onHandleCancel}
            centered="true"
            footer={[
                <Button key="back" onClick={onHandleCancel}>
                    뒤로가기
                </Button>,
                <Button key="submit" type="primary" loading={isLoggingIn} onClick={onSubmitForm}>
                    로그인
                </Button>,
            ]}
        >
            <Form
            {...formItemLayout}
            layout="horizontal"
            form={form}
            onFinish={onSubmitForm}
            >
                <Form.Item
                name="id"
                label="아이디"
                >
                    <Input placeholder="아이디" prefix={<UserOutlined className="site-form-item-icon"/>}/>
                </Form.Item>
                <Form.Item
                name="password"
                label="비밀번호"
                >
                    <Input placeholder="비밀번호" prefix={<LockOutlined className="site-form-item-icon"/>}/>
                </Form.Item>
                <Form.Item
                shouldUpdate>
                    <Button 
                        type="primary"
                        htmlType="submit"
                        shape="round"
                        loading={isLoggingIn}
                        disabled={
                            !form.isFieldTouched(true) || form.getFieldValue().filter(({value})=>value.length).length
                        }
                    >
                        로그인
                    </Button>
                </Form.Item>
            </Form>
            <div style={{ marginBottom: '10px' }}>
                <label htmlFor="user-id">아이디</label>
                <Input name="user-id" value={id} onChange={onChangeId} allowClear="true" required prefix={<UserOutlined className="site-form-item-icon" />} placeholder="아이디" />
            </div>
            <div>
                <label htmlFor="user-password">비밀번호</label>
                <Input.Password name="user-password" value={password} onChange={onChangePassword} type="password" onPressEnter={onSubmitForm} required prefix={<LockOutlined className="site-form-item-icon" />} placeholder="비밀번호"/>
            </div>
        </Modal>
    );
};

export default LoginForm;
