import React, { useState, useCallback } from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Menu, Input, Button } from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import LoginForm from './LoginForm';
import ProfileAvatar from './profileAvatar';
import { logoutAction } from '../reducers/user';
import { HomeOutlined, UserOutlined } from '@ant-design/icons';

const AppLayout = ({ children }) => {
    const { isLoggedIn } = useSelector(state => state.user);
    const dispatch = useDispatch();
    const [visible, setVisible] = useState(false);
    const showModal = () => { setVisible(true); }
    const onLogOut = useCallback(() => {
        dispatch(logoutAction);
    }, []);
    return (
        <div>
            <Menu mode="horizontal" style={{ marginBottom: '20px' }} defaultSelectedKeys="home">
                <Menu.Item key="home"><Link href="/"><a><HomeOutlined />구장깨기</a></Link></Menu.Item>
                <Menu.Item key="location">
                    <Input.Search enterButton style={{ verticalAlign: 'middle' }} placeholder="팀 검색하기" />
                </Menu.Item>
                {isLoggedIn && <ProfileAvatar />}
                {isLoggedIn && <Menu.Item key="logout" onClick={onLogOut}>로그아웃</Menu.Item>}
                {!isLoggedIn && <Menu.Item key="login_modal" onClick={showModal}>로그인</Menu.Item>}
                {!isLoggedIn && <Menu.Item key="signup"><Link href="/signup"><a><Button>회원가입</Button></a></Link></Menu.Item>}
            </Menu>
            {children}
            <LoginForm visible={visible} setVisible={setVisible}></LoginForm>
        </div>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node,
};

export default AppLayout;
