import React, { useState, useCallback } from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { useRouter } from 'next/router';
import { Menu, Input, Button, Row, Col, Affix, message } from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import LoginForm from './LoginForm';
import ProfileAvatar from './profileAvatar';
import { logoutAction } from '../reducers/user';
import { HomeOutlined, MessageFilled, UserAddOutlined } from '@ant-design/icons';

const AppLayout = ({ children }) => {
    const { isLoggedIn } = useSelector(state => state.user);
    const dispatch = useDispatch();
    const [visible, setVisible] = useState(false);
    const showModal = () => { setVisible(true); }
    const onLogOut = useCallback(() => {
        dispatch(logoutAction);
    }, []);
    const onApply = () => {
        !isLoggedIn ? message.info("로그인 후 등록할 수 있습니다.") : useRouter().push('/stadium/apply');
    }
    return (
        <Row style={{ width: "100vw" }}>
            <Col xs={{ span: 24 }} sm={{ span: 22, offset: 1 }} md={{ span: 18, offset: 3 }}>
                <Menu mode="horizontal" style={{ marginBottom: '20px' }} defaultSelectedKeys="home" >
                    <Menu.Item key="home" ><Link href="/"><a><HomeOutlined />구장깨기</a></Link></Menu.Item>
                    <Menu.Item key="location">
                        <Input.Search enterButton style={{ verticalAlign: 'middle' }} placeholder="팀 검색하기" />
                    </Menu.Item>
                    <Menu.Item><Button onClick={onApply}>신규구장 등록하기</Button></Menu.Item>
                    {isLoggedIn && <Menu.Item key="logout" onClick={onLogOut} style={{ float: "right" }}>로그아웃</Menu.Item>}
                    {isLoggedIn && <Menu.Item key="profile" style={{ float: "right" }}><ProfileAvatar /></Menu.Item>}
                    {!isLoggedIn && <Menu.Item key="login_modal" onClick={showModal} style={{ float: "right" }}>로그인</Menu.Item>}
                    {!isLoggedIn && <Menu.Item key="signup" style={{ float: "right" }}><Link href="/signup"><a><UserAddOutlined />회원가입</a></Link></Menu.Item>}
                </Menu>
                <LoginForm visible={visible} setVisible={setVisible}></LoginForm>
            </Col>
            <Col span={24}>
                {children}
            </Col>
            {isLoggedIn && <Affix style={{ position: "absolute", right: '5vw', bottom: '10vh' }} ><Button icon={<MessageFilled style={{ color: "#2793fb", fontSize: "50px" }} />} style={{ height: "50px", width: "50px" }} ghost="true"></Button> </Affix>}
        </Row>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node,
};

export default AppLayout;
