import React, { useEffect } from 'react';
import { withRouter } from 'next/router';
import { useSelector, useDispatch } from 'react-redux';
import { changeLocationAction } from '../reducers/location';
import { makeMarker } from '../util/makeMarker';
import { notification, message, Card } from 'antd';
import { LoadingOutlined, CloseOutlined } from '@ant-design/icons';
const Maps = (props) => {
    const { latitude, longitude, stadiumList } = useSelector(state => state.location);
    const dispatch = useDispatch();
    let options;
    let map;
    useEffect(() => {
        console.log("useEffect after change stadiumList");
        stadiumList.map((c) => {
            const marker = new kakao.maps.Marker(
                {
                    map: map,
                    position: new kakao.maps.LatLng(c.lat, c.lng)
                }
            )
            var content = '<div class="wrap">' +
                '    <div class="info">' +
                '        <div class="title">' +
                '            카카오 스페이스닷원' +
                '            <div class="close" onclick="closeOverlay()" title="닫기"></div>' +
                '        </div>' +
                '        <div class="body">' +
                '            <div class="img">' +
                '                <img src="https://cfile181.uf.daum.net/image/250649365602043421936D" width="73" height="70">' +
                '           </div>' +
                '            <div class="desc">' +
                '                <div class="ellipsis">제주특별자치도 제주시 첨단로 242</div>' +
                '                <div class="jibun ellipsis">(우) 63309 (지번) 영평동 2181</div>' +
                '                <div><a href="https://www.kakaocorp.com/main" target="_blank" class="link">홈페이지</a></div>' +
                '            </div>' +
                '        </div>' +
                '    </div>' +
                '</div>';
            const overlay = new kakao.maps.CustomOverlay({
                content: content,
                map: null,
                position: marker.getPosition()
            });
            (function (marker, overlay) {
                kakao.maps.event.addListener(marker, 'click', function () {
                    overlay.setMap(map);
                });
                function closeOverlay() {
                    overlay.setMap(null);
                }
            })(marker, overlay);
        })
    }, [stadiumList])
    useEffect(
        () => {
            //최초 마운트 시
            // 지도 위치에 따른 데이터 가져와야 해 redux-saga
            options = {
                center: new kakao.maps.LatLng(latitude, longitude),
                level: 8
            };
            map = new kakao.maps.Map(document.getElementById("mapContainer"), options);
            map.addControl(new kakao.maps.MapTypeControl(), kakao.maps.ControlPosition.TOPRIGHT);

            stadiumList.map((c) => {
                const marker = new kakao.maps.Marker(
                    {
                        map: map,
                        position: new kakao.maps.LatLng(c.lat, c.lng)
                    }
                )
                var content = '<div class="wrap">' +
                    '    <div class="info">' +
                    '        <div class="title">' +
                    '            카카오 스페이스닷원' +
                    '            <div class="close" onclick="closeOverlay()" title="닫기"></div>' +
                    '        </div>' +
                    '        <div class="body">' +
                    '            <div class="img">' +
                    '                <img src="https://cfile181.uf.daum.net/image/250649365602043421936D" width="73" height="70">' +
                    '           </div>' +
                    '            <div class="desc">' +
                    '                <div class="ellipsis">제주특별자치도 제주시 첨단로 242</div>' +
                    '                <div class="jibun ellipsis">(우) 63309 (지번) 영평동 2181</div>' +
                    '                <div><a href="https://www.kakaocorp.com/main" target="_blank" class="link">홈페이지</a></div>' +
                    '            </div>' +
                    '        </div>' +
                    '    </div>' +
                    '</div>';
                const overlay = new kakao.maps.CustomOverlay({
                    content: content,
                    map: null,
                    position: marker.getPosition()
                });
                (function (marker, overlay) {
                    kakao.maps.event.addListener(marker, 'click', function () {
                        overlay.setMap(map);
                    });
                    function closeOverlay() {
                        overlay.setMap(null);
                    }
                })(marker, overlay);
            })
            kakao.maps.event.addListener(map, 'dragend', function () {
                let latlng = map.getCenter();
                dispatch(changeLocationAction({ latitude: latlng.getLat(), longitude: latlng.getLng(), }));
            });
            if (props.router.query.arr) {
                const arr = props.router.query.arr.split(",");
                if (arr[0] === "success") {
                    message.success(arr[1]);
                } else if (arr[0] === "warn") {
                    notification.destroy();
                    notification.open({ message: "현재위치로 탐색하시려면?", description: "이전에 위치정보 제공을 동의하시지 않은 경우, 주소창 앞 자물쇠 버튼을 클릭하여 수정하여 주세요.(Internet Explorer에서는 사용하실 수 없습니다.)", duration: 0 })
                } else {
                    message.error(arr[1], 4);
                }
            }
        }, []
    );
    return (
        <div id="mapContainer" style={{ height: '70vh', textAlign: 'center' }}>
            <LoadingOutlined style={{ margin: '0 auto', width: '10vh', height: '10vh' }} />
        </div>
    );
};

export default withRouter(Maps);
