import React, {useEffect } from 'react';
import { useRouter, withRouter, } from 'next/router'
import Router from 'next/router';
import {useDispatch, useSelector} from 'react-redux';
import {Row, Col, List, Skeleton, Typography, Button} from 'antd';
import {SEARCH_TEAMS_REQUEST} from '../../reducers/team'
import {MessageOutlined, LikeOutlined, StarOutlined} from '@ant-design/icons'

const Search = (props) =>{
    const dispatch = useDispatch();
    const {teamList, isSearched, query, isSearching } = useSelector(state => state.team);
    let hashPage = 1;
    useEffect(
        ()=>{
            dispatch({type:SEARCH_TEAMS_REQUEST, data:{"query": props.router.query.q}});
        }
    ,[props.router.query.q]);
    useEffect(
        ()=>{
            hashPage = window.location.hash.substr(1);
        },[hashPage]
    )
    const goToTeamInfo = (req)=>{
        Router.push(`/team/${req}`,`/team/[id]`)
    }
    console.log('------------------------------------');
    console.log(teamList);
    console.log('------------------------------------');
    return(
        <div>
            <Row>
                <Col xs={{ span: 24 }} sm={{ span: 22, offset: 1 }} md={{ span: 18, offset: 3 }}>
                    <Typography.Title level={4}>"{query}"에 대한 검색 결과입니다.</Typography.Title>
                    {
                        isSearched &&
                        <List
                            loading={isSearching}
                            itemLayout="vertical"
                            // pagination={{onChange:page=>{window.location.hash = page}, pageSize: 10, defaultCurrent:{hashPage}}}
                            dataSource={teamList}
                            footer={
                                <div>
                                    <b>건</b>의 검색결과
                                </div>
                            }
                            renderItem={item=>(
                                <List.Item
                                    key={item.req}
                                    // actions={[
                                    //     <span>{StarOutlined}{item.occupation}</span>,
                                    //     <Button icon={MessageOutlined} type="link" ></Button>
                                    // ]}
                                >
                                    <List.Item.Meta 
                                        title={<a onClick={goToTeamInfo(`${item.req}`)}>{item.name}</a> }
                                        // description={
                                        //     <div>
                                        //         <Tag key={item.location}>#{item.location}</Tag>
                                        //         <Tag key={item.recruit}>#{item.recruit === "Y" && "모집중"}</Tag>
                                        //     </div>
                                        // }
                                    />
                                        {item.description}
                                </List.Item>
                                )
                            }
                        />
                    }

                </Col>
            </Row>
        </div>
    );
}
export default withRouter(Search);
