export async function getLocation() {
    function loc() {
        return new Promise((resolve, reject) => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(function (pos) {
                    resolve(["success", "현재 접속위치를 중심으로 지도를 활성화하였습니다.", pos.coords.latitude, pos.coords.longitude]);
                }, function (pos) {
                    console.log(pos);
                    resolve(["warn", "위치정보제공을 동의하시고 현재위치 주변 구장을 확인하세요."]);
                }, { timeout: 3000, enableHighAccuracy: false, maximumAge: 0 });
            } else {
                resolve(["error", "현재 브라우저에서는 현재위치 사용이 불가능합니다."]);
            }
        });
    };
    return await loc();
}

