import React, { useState } from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Menu, Input, Button } from 'antd';
import { useSelector } from 'react-redux';
import LoginForm from './LoginForm';

const AppLayout = ({ children }) => {
    const { isLoggedIn } = useSelector(state => state.user);
    const [visible, setVisible] = useState(false);
    const showModal = () => { setVisible(true); }

    // let toggleLoginState;
    // if (isLoggedIn) {
    //     toggleLoginState = ;
    // } else {
    //     toggleLoginState = <></>;
    // }
    return (
        <div>
            <Menu mode="horizontal" style={{ marginBottom: '20px' }}>
                <Menu.Item key="home"><Link href="/"><a>도장깨기</a></Link></Menu.Item>
                <Menu.Item key="location">
                    <Input.Search enterButton style={{ verticalAlign: 'middle' }} />
                </Menu.Item>
                {isLoggedIn && <Menu.Item key="profile"><Link href="/profile"><a>프로필</a></Link></Menu.Item>}
                {!isLoggedIn && <Menu.Item key="login_modal" onClick={showModal}>로그인</Menu.Item>}
                {!isLoggedIn && <Menu.Item key="signup"><Link href="/signup"><a><Button>회원가입</Button></a></Link></Menu.Item>}
                {/* {toggleLoginState} */}
            </Menu>
            {children}
            <LoginForm visible={visible} setVisible={setVisible}></LoginForm>
        </div>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node,
};

export default AppLayout;
