import React, { useCallback } from 'react';
import { useSelector } from 'react-redux';
import { Col, Row, Typography, Form, Input, Button, message } from 'antd';
import { useInput } from '../../../util/useInput';

const Apply = () => {
    const [title, onChangeTitle] = useInput('');
    const { isLoggeIn } = useSelector(state => state.user);
    // if (!isLoggeIn) {
    //     message.error("로그인 후 이용하여주세요")
    // }
    const onSubmitForm = useCallback((e) => {
        e.preventDefault();
    })

    return (
        <div style={{ width: '450px', margin: "0 auto", border: "1px solid #dadce0", borderRadius: "8px" }}>
            <Row>
                <Col style={{ width: '100%', textAlign: "center" }}>
                    <Typography.Title level={3} style={{ color: "#202124" }}>근처 구장을 등록하고 챔피언이 되어보세요</Typography.Title>
                    <Typography style={{ color: "#202124", fontWeight: "normal" }}>구장 위치 찾기</Typography>
                </Col>
            </Row>
            <Row style={{ textAlign: "center" }}>
                <Form onSubmit={onSubmitForm} style={{ padding: 10 }} layout="horizontal">
                    <Row gutter={[0, 16]}>
                        <Col style={{ width: 100, display: "inline-block", lineHeight: "32px" }}>
                            구장명
                        </Col>
                        <Col style={{ width: 300, display: "inline-block" }}>
                            <Input name="title" value={title} required onChange={onChangeTitle} placeholder="공식명칭을 적어주세요" />
                        </Col>
                    </Row>
                    <Button type="primary" shape="round" size="large" htmlType="button" onClick={onSubmitForm}>등록 신청하기</Button>
                </Form>
            </Row>
        </div>
    );
}

export default Apply;
