import React, {useEffect } from 'react';
import { useRouter, withRouter, Router } from 'next/router'
import {useDispatch, useSelector} from 'react-redux';
import {Row, Col, List, Skeleton, Typography } from 'antd';
import {SEARCH_TEAMS_REQUEST} from '../../reducers/team'
const Search = (props) =>{
    const dispatch = useDispatch();
    const {list, isSearched, query, isSearching } = useSelector(state => state.team);
    let hashPage = window.location.hash.substr(1);
    // useEffect(
    //     ()=>{
    //         dispatch({type:SEARCH_TEAMS_REQUEST, data:{"query": props.router.query.q}});
    //     }
    // ,[props.router.query.q]);

    return(
        <div>
            <Row>
                <Col xs={{ span: 24 }} sm={{ span: 22, offset: 1 }} md={{ span: 18, offset: 3 }}>
                    <Typography.Title level={4}>"{query}"에 대한 검색 결과입니다.</Typography.Title>
                    <List
                        loading={isSearching}
                        itemLayout="vertical"
                        pagination={{onChange:page=>{window.location.hash = page}, pageSize: 10, defaultCurrent:{hashPage}}}
                        dataSource={list}
                        footer={
                            <div>
                                <b>{list.length}건</b>의 검색결과
                            </div>
                        }
                        renderItem={item=>(
                            <List.Item
                                key={item.req}
                            >

                            </List.Item>
                            )
                        }
                    />
                </Col>
            </Row>
        </div>
    );
}
export default withRouter(Search);
