import React, {useEffect } from 'react';
import { useRouter, withRouter, Router } from 'next/router'
import {useDispatch, useSelector} from 'react-redux';
import {Row, Col, List, Skeleton, Typography, Space } from 'antd';
//import {SEARCH_TEAMS_REQUEST} from '../../reducers/team'
import {MessageOutlined, LikeOutlined, StarOutlined} from '@ant-design/icons'

const Search = (props) =>{
    const dispatch = useDispatch();
    const {teamList, isSearched, query, isSearching } = useSelector(state => state.team);
    let hashPage = 1;
    // useEffect(
    //     ()=>{
    //         dispatch({type:SEARCH_TEAMS_REQUEST, data:{"query": props.router.query.q}});
    //     }
    // ,[props.router.query.q]);
    useEffect(
        ()=>{
            hashPage = window.location.hash.substr(1);
        }
    )
    const IconText = ({ icon, text }) => (
        <Space>
            {React.createElement(icon)}
            {text}
        </Space>
    );

    return(
        <div>
            <Row>
                <Col xs={{ span: 24 }} sm={{ span: 22, offset: 1 }} md={{ span: 18, offset: 3 }}>
                    <Typography.Title level={4}>"{query}"에 대한 검색 결과입니다.</Typography.Title>
                    <List
                        loading={isSearching}
                        itemLayout="vertical"
                        pagination={{onChange:page=>{window.location.hash = page}, pageSize: 10, defaultCurrent:{hashPage}}}
                        dataSource={teamList}
                        footer={
                            <div>
                                <b>건</b>의 검색결과
                            </div>
                        }
                        renderItem={item=>(
                            <List.Item
                                key={item.req}
                                actions={[
                                    <IconText icon={StarOutlined} text={item.occupation} key="list-vertical-star-o"/>,
                                    <IconText icon={MessageOutlined} key="list-vertical-message-o"/>,
                                ]}
                            >
                                <List.Item.Meta 
                                    title={<a onClick={Router.push(`/team/${item.req}`,`/team/[id]`)}>{item.name}</a> }
                                    description={
                                        <div>
                                            <Tag key={item.location}>#{item.location}</Tag>
                                            <Tag key={item.recruit}>#{item.recruit === "Y" && "모집중"}</Tag>
                                        </div>
                                    }
                                />
                                {/* Router.push(`/stadium/register/details?data=${convey_data}`, '/stadium/register/details'); */}
                                    {item.description}
                            </List.Item>
                            )
                        }
                    />
                </Col>
            </Row>
        </div>
    );
}
export default withRouter(Search);
