import React, { useState, useCallback } from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Menu, Input, Button, Row, Col } from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import LoginForm from './LoginForm';
import ProfileAvatar from './profileAvatar';
import { logoutAction } from '../reducers/user';
import { HomeOutlined } from '@ant-design/icons';

const AppLayout = ({ children }) => {
    const { isLoggedIn } = useSelector(state => state.user);
    const dispatch = useDispatch();
    const [visible, setVisible] = useState(false);
    const showModal = () => { setVisible(true); }
    const onLogOut = useCallback(() => {
        dispatch(logoutAction);
    }, []);
    return (
        <Row>
            <Col xs={{ span: 24 }} sm={{ span: 22, offset: 1 }} md={{ span: 18, offset: 3 }}>
                <Menu mode="horizontal" style={{ marginBottom: '20px' }} defaultSelectedKeys="home" >
                    <Menu.Item key="home" ><Link href="/"><a><HomeOutlined />구장깨기</a></Link></Menu.Item>
                    <Menu.Item key="location" style={{ width: '25vw' }}>
                        <Input.Search enterButton style={{ verticalAlign: 'middle' }} placeholder="팀 검색하기" />
                    </Menu.Item>
                    {isLoggedIn && <Menu.Item key="logout" onClick={onLogOut} style={{ float: "right" }}>로그아웃</Menu.Item>}
                    {isLoggedIn && <Menu.Item key="profile" style={{ float: "right" }}><ProfileAvatar /></Menu.Item>}
                    {!isLoggedIn && <Menu.Item key="login_modal" onClick={showModal} style={{ float: "right" }}>로그인</Menu.Item>}
                    {!isLoggedIn && <Menu.Item key="signup" style={{ float: "right" }}><Link href="/signup"><a><Button>회원가입</Button></a></Link></Menu.Item>}
                </Menu>
                <LoginForm visible={visible} setVisible={setVisible}></LoginForm>
            </Col>
            <Col span={24}>
                {children}
            </Col>
        </Row>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node,
};

export default AppLayout;
