import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {LOAD_LIST_REQUEST} from '../../reducers/team';
import {Table, Col, Row, Typography} from 'antd';


const columns = [
    {
        title: '순위',
        width: '70',
        dataIndex: 'rank',
        fixed: 'left',
        align:'center',
        ellipsis:true,
        sorter: (a, b) => a.rank - b.rank,
    },
    {
        title: '팀 명',
        width: 100,
        dataIndex: 'name',
        fixed: 'left',
        align:'center',
        ellipsis:true,
    },
    {
        title: '활동지역',
        width: 100,
        align:'center',
        dataIndex: 'location',
        filters: [
            {
            text: '서울',
            value: '서울',
            },
            {
            text: '경기',
            value: '경기',
            },
            {
            text: '부산',
            value: '부산',
            },

        ],
        onFilter:(value, record) =>record.location.indexOf(value) === 0,
    },
    {
        title: '점령 구장 수',
        dataIndex: 'occupation',
        width: 80,
        align:'center',
    },
    {
        title: '회원모집',
        dataIndex: 'recruit',
        width: 80,
        align:'center',
    },
    {
        title: '비고',
        key: 'operation',
        fixed: 'right',
        width: 100,
        render: () => <span><a>action</a><a>message</a></span>,
    },
];

const Ranking = () =>{
    const dispatch = useDispatch();
    const { list,  isLoading} = useSelector(state => state.team);
    useEffect(
        ()=>{
            dispatch({
                type:LOAD_LIST_REQUEST,
            })
    },[])
    return(
        <Row>
            <Col xs={{ span: 22, offset:1 }} sm={{ span: 22, offset: 1 }} md={{ span: 18, offset: 3 }}>
                <Typography.Title level={3}>
                    구장깨기 순위
                </Typography.Title>
                <Table
                loading={isLoading}
                columns={columns}
                pagination
                dataSource={list}
                >
                </Table>
            </Col>
        </Row>
    );
}

export default Ranking;
