import React from 'react';
import { useSelector, } from 'react-redux';
import { Row, Col, Spin } from 'antd';
import Maps from './Maps';
import StadiumList from './StadiumList';
// import StadiumInfo from './StadiumInfo';

const BattleMap = () => {
    const { stadiumList, isChangedLocation, isChangingLocation } = useSelector(state => state.location);
    return (
        <div>
            <Row gutter={[20, 16]}>
                <Col xs={{ span: 22, offset: 1 }} md={{ span: 15, offset: 2 }}>
                    {/* 지도 */}
                    {isChangingLocation && <Spin size="large"/>}
                    {isChangedLocation && <Maps list={stadiumList} />}
                </Col>

                <Col xs={{ span: 22, offset: 1 }} md={{ span: 5, offset: 0 }}>
                    {/* 리스트 */}
                    <StadiumList list={stadiumList} />
                </Col>

                {/* <Col xs={{ span: 22, offset: 1 }} md={{ span: 20, offset: 2 }}>
                    <StadiumInfo />
                </Col> */}
            </Row>
        </div>
    );
};

export default BattleMap;
