import { all, delay, fork, put, takeEvery } from 'redux-saga/effects';
import axios from 'axios';
import {
    SELECT_TEAM_FAILURE,
    SELECT_TEAM_REQUEST,
    SELECT_TEAM_SUCCESS,
    ENROLL_TEAM_FAILURE,
    ENROLL_TEAM_REQUEST,
    ENROLL_TEAM_SUCCESS,
} from '../reducers/team';

function selectAPI() {
  // 서버에 요청을 보내는 부분
    return axios.post('/team');
}

function* select() {
    try {
        // yield call(selectAPI);
        yield delay(2000);
        yield put({ // put은 dispatch 동일
            type: SELECT_TEAM_SUCCESS,
        });
    } catch (e) {
        console.error(e);
        yield put({
            type: SELECT_TEAM_FAILURE,
            error:e,
        });
    }
}

function* watchSelect() {
    yield takeEvery(SELECT_TEAM_REQUEST, select);
}

function enrollAPI() {
  // 서버에 요청을 보내는 부분
    return axios.post('/team/register');
}

function* enroll() {
    try {
        // yield call(enrollAPI);
        yield delay(2000);
        throw new Error('에러에러에러');
        yield put({ // put은 dispatch 동일
            type: ENROLL_TEAM_SUCCESS,
        });
    } catch (e) { // loginAPI 실패
        console.error(e);
        yield put({
            type: ENROLL_TEAM_FAILURE,
            error: e,
        });
    }
}

function* watchEnroll() {
    yield takeEvery(ENROLL_TEAM_REQUEST, enroll);
}

export default function* userSaga() {
    yield all([
        fork(watchSelect),
        fork(watchEnroll),
    ]);
}
