import React, {useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {Comment, List, Avatar, Form, Button, Input, Rate} from 'antd';
import {ADD_COMMENT_REQUEST} from '../reducers/stadium'

const CommentList = ({ comments }) => (
    <List
        dataSource={comments}
        itemLayout="horizontal"
        renderItem={props => <Comment {...props} actions={<Rate disabled value={props.rating}></Rate>}/>}
    />
);

const Editor = ({ onChange, onSubmit, isAddingComment, value }) => (
    <div>
        <Form.Item>
            <Input.TextArea rows={4} onChange={onChange} value={value} />
        </Form.Item>
        <Form.Item>
            <Button htmlType="submit" loading={isAddingComment} onClick={onSubmit} type="primary">
                등록
            </Button>
        </Form.Item>
    </div>
);

const StadiumComment = () =>{
    const dispatch = useDispatch();
    const { comments, isAddingComment } = useSelector(state=> state.stadium);
    const { isLoggedIn, me } = useSelector(state => state.user);
    const [comment, onChangeComment] = useState('');

    const handleSubmit = ()=>{
        dispatch({
            type:ADD_COMMENT_REQUEST,
            data:{
                datetime: new Date().toLocaleDateString(),
                content: comment,
                avatar:me.nickname,
                author:me.nickname,
                rating:5,
            }
        });
        onChangeComment('');
    }
    const handleChange= (e)=>{
        onChangeComment(e.target.value);
    }

    return(
        <div>
            {comments.length > 0 && <CommentList comments={comments} />}
            <Comment
            avatar = {isLoggedIn && <Avatar shape="circle">{me.nickname}</Avatar> }
            content = {
                isLoggedIn &&
                <div>
                    <Form.Item>
                        <Input.TextArea rows={4} onChange={handleChange} value={comment} />
                    </Form.Item>
                    <Form.Item>
                        <Button htmlType="submit" loading={isAddingComment} onClick={handleSubmit} type="primary">
                            등록
                        </Button>
                    </Form.Item>
                </div>
            }
            />
        </div>
    );
}
export default StadiumComment;
