import React, { useEffect } from 'react';
import { List, Badge, Collapse } from 'antd';

const data = [
    {
        req: 1,
        title: "누상동 다목적 운동장",
        description: "인도어풋살장",
        content: "우레탄이 아닌 풋살장입니다"
    },
    {
        req: 2,
        title: "누상동 다목적 운동장",
        description: "인도어풋살장",
        content: "우레탄이 아닌 풋살장입니다"
    },
    {
        req: 3,
        title: "누상동 다목적 운동장",
        description: "인도어풋살장",
        content: "우레탄이 아닌 풋살장입니다"
    },
    {
        req: 4,
        title: "누상동 다목적 운동장",
        description: "인도어풋살장",
        content: "우레탄이 아닌 풋살장입니다"
    }
];
const StadiumList = () => {
    let width;
    useEffect(() => {
        width = window.innerWidth;
        console.log('------------------------------------');
        console.log(width);
        console.log('------------------------------------');
    }, [])
    return (
        <div>
            {width < 576 && <Collapse>
                <Collapse.Panel header="구장 리스트">
                    <List
                        style={{ height: '70vh' }}
                        size="small"
                        itemLayout="vertical"
                        pagination={{
                            onChange: page => {
                                console.log(page);
                            },
                            showLessItems: true,
                        }}
                        dataSource={data}
                        renderItem={item => (
                            <List.Item
                                key={item.req}
                            >
                                <List.Item.Meta
                                    title={item.title}
                                    description={item.description}
                                />
                                {item.content}
                            </List.Item >
                        )}
                    >

                    </List >
                </Collapse.Panel>
            </Collapse>}
            {width >= 576 && <List
                header={
                    <div>
                        <Badge count={109} style={{ backgroundColor: '#52c41a', top: '-10px', right: '-20px' }}>
                            <b>구장 리스트</b>
                        </Badge>
                    </div>
                }
                style={{ height: '70vh' }}
                size="small"
                itemLayout="vertical"
                pagination={{
                    onChange: page => {
                        console.log(page);
                    },
                    pageSize: 4,
                    showLessItems: true,
                }}
                dataSource={data}
                renderItem={item => (
                    <List.Item
                        key={item.req}

                    >
                        <List.Item.Meta
                            title={item.title}
                            description={item.description}
                        />
                        {item.content}
                    </List.Item >
                )}
            >

            </List >}
        </div>
    );
}

export default StadiumList;
