import React, { useCallback, useState } from 'react';
import { Button, Input, Modal } from 'antd';
import { useInput } from '../util/useInput';
import { useDispatch, useSelector } from 'react-redux';
import { loginAction } from '../reducers/user';

const LoginForm = (props) => {
    const [id, onChangeId] = useInput('');
    const [password, onChangePassword] = useInput('');
    const [loading, setLoading] = useState(false);
    const { visible, setVisible } = props;
    const dispatch = useDispatch();


    const onHandleCancel = (() => {
        setVisible(false);
    })
    const onSubmitForm = useCallback((e) => {
        e.preventDefault();
        setLoading(true);
        console.log({
            id, password,
        });
        dispatch(loginAction({
            id,
            password,
        }));
        if (useSelector(state => state.user.isLoggedIn)) {
            setLoading(false);
            setVisible(false);
        }
        // setTimeout(() => {

        // }, 3000);
    }, [id, password]);

    return (
        <Modal
            title="FutChall로그인"
            visible={visible}
            onOk={onSubmitForm}
            onCancel={onHandleCancel}
            centered="true"
            footer={[
                <Button key="back" onClick={onHandleCancel}>
                    뒤로가기
                </Button>,
                <Button key="submit" type="primary" loading={loading} onClick={onSubmitForm}>
                    로그인
                </Button>,
            ]}
        >
            <div style={{ marginBottom: '10px' }}>
                <label htmlFor="user-id">아이디</label>
                <Input name="user-id" value={id} onChange={onChangeId} allowClear="true" required />
            </div>
            <div>
                <label htmlFor="user-password">비밀번호</label>
                <Input.Password name="user-password" value={password} onChange={onChangePassword} type="password" onPressEnter={onSubmitForm} required />
            </div>
        </Modal>
    );
};

export default LoginForm;
