import React, {useEffect, useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useRouter } from 'next/router'
import {Skeleton, Col, Row, Tabs, Button, message, Descriptions, Tag, Typography} from 'antd';
// import StadiumInfo from '../../components/StadiumInfo';
import {selectStadiumAction}from '../../reducers/stadium';
import { CopyOutlined, QuestionCircleOutlined } from '@ant-design/icons';
const Stadium = () =>{
    const router = useRouter();
    const dispatch = useDispatch();
    const { id } = router.query
    dispatch(selectStadiumAction({ req: id }));
    const [loading, setLoading] = useState(true);
    const { req, title, lat, lng, address, time, light, special, description,} = useSelector(state => state.stadium);
    useEffect(
        ()=>{
            const options = {
                center: new kakao.maps.LatLng(lat, lng),
                level: 3
            };
            const map = new kakao.maps.Map(document.getElementById("stadiumAddress"), options);
            map.addControl(new kakao.maps.MapTypeControl(), kakao.maps.ControlPosition.TOPRIGHT);
            const marker = new kakao.maps.Marker(
                {
                    map: map,
                    position: new kakao.maps.LatLng(lat, lng)
                }
            )
        }
    );
    return(
        <div>
            <Row>
                <Col xs={{ span: 22, offset:1 }} sm={{ span: 22, offset: 1 }} md={{ span: 18, offset: 3 }}>
                    <Typography.Title level={3}>{title}</Typography.Title>
                    <Tabs  tabBarExtraContent={<Button onClick={()=>{message.info("링크가 복사되었습니다.")}}><CopyOutlined />Copy</Button> }>
                        <Tabs.TabPane tab="상세정보" key="1" >
                            <Descriptions
                            column={{ xxl: 4, xl: 3, lg: 3, md: 3, sm: 2, xs: 1 }}
                            bordered={true}
                            >
                                <Descriptions.Item label="주소">
                                    {address}
                                </Descriptions.Item>
                                <Descriptions.Item label="사용시간">
                                    {time}
                                </Descriptions.Item>
                                <Descriptions.Item label="라이트 여부">
                                    {light}
                                </Descriptions.Item>
                                <Descriptions.Item label="특징">
                                {special.map((c) => {
                                    return (<Tag key={c}>#{c}</Tag>)
                                })}
                                </Descriptions.Item>
                                <Descriptions.Item label="소개">
                                    {description}
                                </Descriptions.Item>
                            </Descriptions>
                        </Tabs.TabPane>
                        <Tabs.TabPane tab="후기" key="2">
                                {/* {"준비중입니다."} */}
                        </Tabs.TabPane>
                        <Tabs.TabPane tab="사진" key="3">
                                {/* {"준비중입니다."} */}
                        </Tabs.TabPane>
                    </Tabs>
                </Col>
            </Row>
            <Row>
                <Col xs={{ span: 22, offset:1 }} sm={{ span: 22, offset: 1 }} md={{ span: 18, offset: 3 }}>
                    <div id="stadiumAddress" style={{width:'100%', height:'500px'}}></div>
                </Col>
            </Row>
        </div>
    );
}
export default Stadium;
